import 'dart:convert'; // Para base64Encode
import 'dart:io'; // Para manejar archivos
import 'dart:math';
import 'package:image_picker/image_picker.dart';
import '../models/photo.dart';

class PhotoService {
  final ImagePicker _picker = ImagePicker(); // Instancia de ImagePicker

  Future<Photo?> addPhoto(String type, String user) async {
    try {
      if (Platform.isAndroid) {
        // Seleccionar una imagen usando la c√°mara
        final XFile? image =
            await _picker.pickImage(source: ImageSource.camera);

        if (image != null) {
          // Leer la imagen como bytes
          final bytes = await File(image.path).readAsBytes();

          // Convertir la imagen a Base64
          final base64Image = base64Encode(bytes);

          // Crear un nuevo objeto Photo
          int randomNumber = Random().nextInt(1000);
          return Photo(
            type: type,
            user: user,
            randomNumber: randomNumber,
            dateAdded: DateTime.now(),
            base64Image: base64Image,
          );
        }
      } else if (Platform.isLinux) {
        // Generar una foto de prueba en Linux
        int randomNumber = Random().nextInt(1000);
        return Photo(
          type: type,
          user: user,
          randomNumber: randomNumber,
          dateAdded: DateTime.now(),
          base64Image:
              '',
        );
      }
    } catch (e) {
      print('Error al tomar la foto: $e');
    }
    return null; // Si no se pudo tomar la foto
  }
}
